import config from './config';

export { resizeImage, formatPrice } from 'storejars-react-toolkit';

export function getAsset(asset: string) {
  return config.basePath + '/' + asset;
}

export const businessTypes = [
  'Fashion',
  'Electronics',
  'Cosmetics',
  'Automobiles',
  'Construction',
  'Foods',
  'Kitchen Appliances',
  'Interior Decorations',
  'Perfumes',
  'Drugs and Dispensaries',
  'Others',
  'Multi Products',
];

export function getAppLink(ios) {
  if (ios) {
    return 'https://apps.apple.com/app/apple-store/id1584973436?pt=120992111&ct=Site&mt=8';
  } else {
    return 'https://play.google.com/store/apps/details?id=com.storejars';
  }
}
